import '../index.css';
import React from "react";
import Header from './ Header';
import Main from './Main';
import Footer from './Footer';

function App() {
    const [isEditProfilePopupOpen, handleEditProfileClick] = React.useState(false);
    const openPopup = (popup) => {
        popup.classList.add('popup_opened');
    };

    const handleEditAvatarClick = () => {
        openPopup(document.querySelector('.popup_type_edit-avatar'));
    };

    const handleAddCardClick = () => {
        openPopup(document.querySelector('.popup_type_add-card'));
    };

    const isAddPlacePopupOpen = {
        isOpen: false
    };
    const isEditAvatarPopupOpen = {
        isOpen: false
    };

    return (
        <div className="page">
            <Header/>
            <Main onEditProfile={handleEditProfileClick} onAddCard={handleAddCardClick}
                  onEditAvatar={handleEditAvatarClick}/>
            <template id="element-template">
                <article className="element">
                    <img className="element__photo" src="mesto-react/src/components/App#" alt=""/>
                    <button className="element__trash" type="button" aria-label="Удалить фото"/>
                    <h2 className="element__text"></h2>
                    <div className="element__like-block">
                        <button className="element__like" type="button" aria-label="Поставить лайк"/>
                        <p className="element__like-number"/>
                    </div>
                </article>
            </template>
            <Footer/>
        </div>)
}

export default App;
