import Api from '../utils/Api';
import Card from "./Card";
import React from 'react';
import {CurrentUserContext} from '../contexts/CurrentUserContext';

function Main(props) {
    const currentUser = React.useContext(CurrentUserContext);
    const [cards, createCards] = React.useState([]);

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);
        console.log(isLiked);
        Api.changeLikeCardStatus(card._id, !isLiked)
            .then(async (newCard) => {
            await console.log(newCard);
                cards.map((card) => card._id === card._id ? newCard : card)
            /*createCards((cards) => );*/
        });
    }

    React.useEffect(() => {
        Api.getInitialCards()
            .then((res => {
                createCards(res)
            }))
            .catch((err) => console.log(err))
    }, []);

    return (
        <main>
            <section className="profile">
                <div className="avatar-block" onClick={props.onEditAvatar}>
                    <img className="avatar" src={currentUser.avatar} alt="Аватaр"/>
                    <div className="avatar-edit"></div>
                </div>
                <div className="profile__info">
                    <p className="profile__name">{currentUser.name}</p>
                    <button className="profile__edit-button" type="button"
                            aria-label="Изменить информацию" onClick={props.onEditProfile}/>
                    <p className="profile__job">{currentUser.about}</p>
                </div>
                <button className="profile__add-button" type="button" aria-label="Добавить"
                        onClick={props.onAddCard}/>
            </section>
            <section className="elements">
                {cards.map((el) => (
                    <Card i={el._id} card={el}
                          onCardClick={props.handleCardClick} onCardLike={handleCardLike}/>
                ))}
            </section>
        </main>
    )
}

export default Main;
