import Api from '../utils/Api';
import Card from "./Card";
import React from 'react';
import {CurrentUserContext} from '../contexts/CurrentUserContext';

function Main(props) {
    const currentUser = React.useContext(CurrentUserContext);
    const [cards, createCards] = React.useState([]);

    const handleCardLike = async (card) => {
        const isLiked = card.likes.some(likes => likes._id === currentUser._id);
        await Api.changeLikeCardStatus(card._id, !isLiked)
            .then((newCard) => {
                createCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }

    /*const handleButtonClickLike = async (result, id, func) => {
        if (!result) {
            await Api.addLike(id)
                .then(res => {
                    func(res.likes.length)
                })
                .catch((err) => {
                    console.log(err);
                });
        } else {
            await Api.deleteLike(id)
                .then(res => {
                    func(res.likes.length)
                })
                .catch((err) => {
                    console.log(err);
                });
        }
    };*/

    React.useEffect(() => {
        Api.getInitialCards()
            .then((res => {
                createCards(res)
            }))
            .catch((err) => console.log(err))
    }, [currentUser]);

    return (
        <main>
            <section className="profile">
                <div className="avatar-block" onClick={props.onEditAvatar}>
                    <img className="avatar" src={currentUser.avatar} alt="Аватaр"/>
                    <div className="avatar-edit"></div>
                </div>
                <div className="profile__info">
                    <p className="profile__name">{currentUser.name}</p>
                    <button className="profile__edit-button" type="button"
                            aria-label="Изменить информацию" onClick={props.onEditProfile}/>
                    <p className="profile__job">{currentUser.about}</p>
                </div>
                <button className="profile__add-button" type="button" aria-label="Добавить"
                        onClick={props.onAddCard}/>
            </section>
            <section className="elements">
                {cards.map((el) => (
                    <Card i={el._id} card={el} onCardLike={handleCardLike}
                          onCardClick={props.handleCardClick}/>
                ))}
            </section>
        </main>
    )
}

export default Main;
