import React from "react";

class Card extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cardState: true,
        }
    }

    deleteCard = () => {
        this.setState({cardState: !this.state.cardState})
    }

    setUserLike() {
        return this.props.likes.owner.some(el => el._id === this.props.user)
    }

    render() {
        if (this.state.cardState) {
            return (
                <article className="element" key={this.props.i}>
                    <img className="element__photo" src={this.props.link} alt={`Изображение ${this.props.name}`}/>
                    {(!(this.props.user === this.props.owner)) && (
                        <button key={'button'} className="element__trash" type="button" aria-label="Удалить фото"
                                onClick={this.deleteCard}/>)}
                    <h2 className="element__text">{this.props.name}</h2>
                    <div className="element__like-block">
                        <button className={`element__like ${this.setUserLike ? 'element__like_active' : '' }`} type="button" aria-label="Поставить лайк"/>
                        <p className="element__like-number">{this.props.likes.length}</p>
                    </div>
                </article>)
        }
    }
}

export default Card;
