import React from "react";
import Header from './ Header';
import Main from './Main';
import Footer from './Footer';

function App() {
    const [isOpen, isEditProfilePopupOpen] = React.useState(false);

    const isAddPlacePopupOpen = {
        isOpen: false
    };

    const isEditAvatarPopupOpen = {
        isOpen: false
    };

    const handleEditAvatarClick = () => {
        isEditProfilePopupOpen.se
    };

    const handleEditProfileClick = () => {
        isEditProfilePopupOpen.isOpen =!isEditProfilePopupOpen.isOpen
        console.log('function')
        console.log(isEditProfilePopupOpen.isOpen)
    };

    const handleAddCardClick = () => {
        isAddPlacePopupOpen.isOpen =!isAddPlacePopupOpen.isOpen;
        console.log('function')
        console.log(isAddPlacePopupOpen.isOpen)
    };


    return (
        <div className="page">
            <Header/>
            <Main onEditProfile={handleEditProfileClick} onAddCard={handleAddCardClick}
                  onEditAvatar={handleEditAvatarClick} /*isEditProfilePopupOpen={isEditProfilePopupOpen} isAddPlacePopupOpen={isAddPlacupOpen} isEditAvatarPopupOpen={isEditAvatarPopupOpen}ePop*//>
            <template id="element-template">
                <article className="element">
                    <img className="element__photo" src="mesto-react/src/components/App#" alt=""/>
                    <button className="element__trash" type="button" aria-label="Удалить фото"/>
                    <h2 className="element__text"></h2>
                    <div className="element__like-block">
                        <button className="element__like" type="button" aria-label="Поставить лайк"/>
                        <p className="element__like-number"/>
                    </div>
                </article>
            </template>
            <Footer/>
        </div>)
}

export default App;
