import React from "react";

class Card extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cardState: true
        }
    }

    deleteCard() {
        this.state.cardState = false;
    }

    render() {
        return (
            <article className="element" key={this.props.i}>
                <img className="element__photo" src={this.props.link} alt={`Изображение ${this.props.name}`} />
                {(!(this.props.user === this.props.owner)) && (<button key={'button'} className="element__trash" type="button" aria-label="Удалить фото" onClick={this.deleteCard}/>)}
                <h2 className="element__text">{this.props.name}</h2>
                <div className="element__like-block">
                    <button className="element__like" type="button" aria-label="Поставить лайк"/>
                    <p className="element__like-number">{this.props.likes.length}</p>
                </div>
            </article>
        )
    }
}

export default Card;
