import React from "react";
import {CurrentUserContext} from '../contexts/CurrentUserContext';

function Card (props) {
    const [cardState, editCardState] = React.useState(true);
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    const likeNumber = props.card.likes.length;
    const classListLike = `element__like ${isLiked ? 'element__like_active' : ''}`;
    const currentUser = React.useState(CurrentUserContext);

    const deleteCard = () => {
        editCardState(false);
    }

    function handleClick() {
        props.onCardClick(props.card);
    }

    function handleLikeClick() {
        props.onCardLike(props.card)
    }

    if (cardState) {
        return (
            <article className="element" key={props.i}>
                <img className="element__photo" src={props.card.link} alt={`Изображение ${props.card.name}`} onClick={handleClick}/>
                {(!(currentUser._id === props.card.owner)) && (
                    <button key={'button'} className="element__trash" type="button" aria-label="Удалить фото"
                            onClick={deleteCard}/>)}
                <h2 className="element__text">{props.card.name}</h2>
                <div className="element__like-block">
                    <button className={classListLike} type="button" aria-label="Поставить лайк" onClick={handleLikeClick}/>
                    <p className="element__like-number">{likeNumber}</p>
                </div>
            </article>)

    }
}

export default Card;
